import {useEffect, useState} from "react";
import Info from './Info.js'
import Entry from './Entry.js'
import './App.css';

function App() {
  const [query, setQuery] = useState("");
  const [data, setData] = useState("");

  useEffect(() => {
    const imageName = encodeURIComponent(query.toLowerCase());
    const url = `https://images-api.nasa.gov/search?q=${imageName}`;
    console.log(url);
    fetch(url)
      .then((r) => r.json())
      .then((r) => setData(r))
      .catch((e) => setData(e));
  }, [query]);

  return (
   <div className="App">
    <Entry action={setQuery} />
    <Info query={query} data={data}/>
   </div>
  );
}

export default App;
