export default function Info({query, data}) {

    return !query || !data ? (
        <p></p>
    ) :!data?.collection.items || data?.collection.items.length === 0 ? (
        <p>No images for {query}</p>
    ) : (
        // <div className="grid-container">
        <main>
            <h2 className="image-name">Results for: {query}</h2>
            <div className="grid-container">
                {data.collection.items.map((item, index) => (
                    <div key={index} className="card">
                        <div className="card-container">
                            <div>{item.data[0].title}</div>
                            <div className="nasa-image"><img src={item.links[0].href} alt={item.data[0].description}/></div>
                            {/* change so that description only displayed if different from title */}
                            <div>{item.data[0].description}</div>
                        </div>
                    </div>
                ))}
            </div>
            {/* can see if media is image type data.collection.items[0].links[0].render */}
            {/* <div>{data.collection.items[0].links[0]}</div> */}

        </main>
    )
    // use grid to display each of the images, 
    // can set limit to max 10 images (2 by 5 grid)
    // or could make the grid change depending on how many results output
}
