import { useEffect, useState } from "react";
import Header from "./Header.js";
import Entry from "./Entry.js";
import Info from "./Info.js";
import {getNasaImage} from "./api.js";

import './App.css';

function App() {
  const [query, setQuery] = useState("");
  const [data, setData] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!query) {
      return;
    }
    async function fetchData() {
      try {
        const image = await getNasaImages(query);
      } catch (error) {
        setError(error);
      }

    }

    const imageName = encodeURIComponent(query.toLowerCase());
    const url = `https://images-api.nasa.gov/search?q=${imageName}`;
    console.log(url);
    fetch(url)
      .then((r) => r.json())
      .then((r) => setData(r))
      .catch((e) => setData(e));
  }, [query]);

  return (
   <div className="App">
    <Header text="NASA Image Lookup"/>
    <Entry action={setQuery} />
    <Info query={query} data={data}/>
   </div>);
}

export default App;
